```javascript
new Vue({
  el: '#app',
  data() {
    return {
      // 
      loading: true,
      // 
      selections: [],
      // 
      single: true,
      // 
      multiple: true,
      // 
      showSearch: true,
      // 
      tableList: [],
      // 
      title: "",
      // 
      open: false,
      // 
      queryParams: {
        name: ''
      },
      // 
      form: {},
      // 
      rules: {
        name: [{required: true, message: "", trigger: "blur"}],
        grade: [{required: true, message: "", trigger: "blur"}],
        points: [{required: true, message: "", trigger: "blur"}],
      }
    };
  },
  created() {
    this.getList();
  },
  methods: {
    /**  */
    getList() {
      this.loading = true;
      // 
      this.loading = false;
    },
    /**  */
    handleQuery() {
      this.getList();
    },
    /**  */
    resetQuery() {
      this.queryParams = {
        name: ''
      };
      this.handleQuery();
    },
    // 
    handleSelectionChange(selection) {
      this.selections = selection;
      this.single = selection.length !== 1;
      this.multiple = !selection.length;
    },
    /**  */
    handleAdd() {
      this.reset();
      this.open = true;
      this.title = "";
    },
    /**  */
    handleUpdate(row) {
      this.reset();
      // 
      this.open = true;
      this.title = "";
    },
    /**  */
    submitForm() {
      this.$refs.form.validate(valid => {
        if (valid) {
          // 
          this.open = false;
          this.getList();
        }
      });
    },
    // 
    cancel() {
      this.open = false;
      this.reset();
    },
    // 
    reset() {
      this.form = {
        id: null,
        name: null,
        grade: null,
        points: null,
        address: null
      };
    },
    /**  */
    handleDelete(row) {
      // 
    }
  }
});
```

