        MyTCPServer tcp = new MyTCPServer();	// 
        tcp.getServer();	// 
    }

    void getServer() {	// 
        try {
            server = new ServerSocket(8998);	// 8998
            System.out.println("");
            while (true) {
                System.out.println("...");
                socket = server.accept();	// 
                System.out.println("");
                reader = new BufferedReader(new InputStreamReader(socket.getInputStream()));	// 
                getClientMessage();	// 
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private void getClientMessage() {	// 
        try {
            while (true) {
                System.out.println("" + reader.readLine());	// 
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
        try {
            if (reader != null) {
                reader.close();
            }
            if (socket != null) {
                socket.close();
            }
            if (server != null) {
                server.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}
