    public void run() {
        while (true) {
            try {
                String data = reader.readLine();	// 
                byte[] dataBytes = data.getBytes();	// 
                DatagramPacket packet = new DatagramPacket(dataBytes, 0, dataBytes.length, new InetSocketAddress(toIP, toPort));	// IP
                socket.send(packet);	// 
                if ("bye".equals(data)) {	// "bye"
                    break;
                }
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
        try {
            reader.close();	// 
        } catch (IOException e) {
            e.printStackTrace();
        }
        socket.close();	// 
    }
}
