    // shiftDown
    for (let i = Math.floor((n - 2) / 2); i >= 0; i--) {
      minHeapShiftDown(i, n);
    }
  }

  function minHeapShiftDown (i, n) {
    // ishiftDownnn
    let j = 2 * i + 1;  // 
    while (j < n) {
      if (j + 1 < n && final[j] > final[j + 1]) {
        j++;	// 
      }
      if (final[i] <= final[j]) {
        break;	// 
      }
      [final[i], final[j]] = [final[j], final[i]];	// 
      i = j;
      j = 2 * i + 1;
    }
  }
}
